sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (newValue === '') {
		return;
	}

	var neededSelectName = "u_what_needed";
	var customOption = g_form.getOption(neededSelectName, "other");
	var currentSelectValue =
		g_form.getValue(neededSelectName) === customOption.value
			? g_form.getValue(neededSelectName)
			: null;

	g_form.clearOptions(neededSelectName);
	var options = g_scratchpad.neededOptions;
	for (var i = 0; i < options.length; ++i) {
		var option = options[i];
		if (option.value.includes(newValue) || option.value.includes("other")) {
			g_form.addOption(neededSelectName, option.value, option.label);
		}
	}

	if (currentSelectValue !== null) {
		g_form.setValue(neededSelectName, currentSelectValue);
	}
}
